package com.tianyuan.hyitrobot.Utils;

import java.io.*;
import java.net.Inet4Address;
import java.net.InetSocketAddress;
import java.net.Socket;

public class SocketClient {
    public static void main(String[] args) throws IOException {
        Socket socket = new Socket(  );
        socket.setSoTimeout(3000);
        socket.connect(new InetSocketAddress(Inet4Address.getLocalHost(),2000),3000);
        System.out.println("已经发起服务器连接，并进入后续流程---");
        System.out.println("客户端信息：" + socket.getLocalAddress() + "port:" + socket.getLocalPort());
        System.out.println("服务器端信息："+ socket.getInetAddress() + "port:" + socket.getPort());
        try {
            todo(socket,null);
        }catch (Exception e){
            System.out.println("异常关闭");
        }
        socket.close();
        System.out.println("客户端退出");
    }
    //发送数据
    private static void todo(Socket client,String json) throws IOException, InterruptedException {
        //获取键盘输入流
        InputStream in = System.in;
        BufferedReader input = new BufferedReader( new InputStreamReader(in));
        //拿到socket输出流 并转换为打印流
        OutputStream outputStream = client.getOutputStream();
        PrintStream socketPrintStream = new PrintStream(outputStream);
        //得到socket 输入流 并转换为BufferedReader
        InputStream inputStream = client.getInputStream();
        BufferedReader socketBufferReader = new BufferedReader(new InputStreamReader(inputStream));
        boolean flag = true;
        json="";
        for (int i=1;i<=100;i++)
        {
            String send=i+" ："+json;
            socketPrintStream.println(send);
            System.out.println("第"+i+"次发送完毕,数据长度："+send.length());
            Thread.sleep(100);
        }

        //        do {
//            // 键盘读取一行
//            String str = input.readLine();
//            //发送到服务器
//            socketPrintStream.println(str);
//            //从服务器读取一行
//            String echo = socketBufferReader.readLine();
//            if ("bye".equalsIgnoreCase(echo)){
//                flag = false;
//            }else {
//                System.out.println(echo);
//            }
//        }while (flag);
        socketPrintStream.close();
        socketBufferReader.close();
    }
}